import React from 'react'
import Header from './Header'
import Nav from './Nav'
import Footer from './Footer'
import Home from './Home'
import NewPost from './NewPost'
import Postpage from './Postpage'
import Missing from './Missing'
import About from './About'
import {useState,useEffect} from 'react'
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';

function App() {

  const [allposts,setAllposts]=useState([
    {
      id: 1,
      title: "Introduction to React Hooks",
      timeAndDate: "May 1, 2023 10:00 AM",
      body: "React Hooks provide a way to add state and lifecycle methods to functional components. They offer a more concise and readable syntax compared to class components."
    },
    {
      id: 2,
      title: "Exploring JavaScript Promises",
      timeAndDate: "June 15, 2023 2:30 PM",
      body: "JavaScript Promises are a powerful feature for handling asynchronous operations. They allow you to write cleaner and more manageable code for tasks like fetching data from APIs."
    },
    {
      id: 3,
      title: "Getting Started with Node.js",
      timeAndDate: "July 5, 2023 11:45 AM",
      body: "Node.js is a runtime environment that allows you to run JavaScript code on the server side. It's popular for building scalable and efficient web applications."
    },
    {
      id: 4,
      title: "Introduction to CSS Grid Layout",
      timeAndDate: "August 20, 2023 4:00 PM",
      body: "CSS Grid Layout is a powerful tool for creating responsive and flexible grid-based layouts in web design. It offers precise control over grid items and their placement."
    },
    {
      id: 5,
      title: "Best Practices for React Component Design",
      timeAndDate: "September 10, 2023 9:15 AM",
      body: "Designing React components involves following best practices such as using functional components, organizing code structure, and optimizing performance for better user experience."
    }
  ]

  )
  const history=useHistory();
  const deletepost=(e,id)=>{

    e.preventDefault()
    const newposts=allposts.filter((ele)=>ele.id!=id)
    setAllposts(newposts)
    history.push('/')
  }
  console.log(allposts)
return (
    <div className="App mt-3 col-lg-5 col-md-10 col-sm-10 shadow-lg border  mx-auto ">
      <Header/>
      <Nav/>
      <Router>
        <Switch >
          <Route exact path="/" >
            {!allposts?<Home posts={allposts}></Home>:<p className='sifs-3 text-primary'>We dont have any posts ...Feel free to create yours</p>}
          </Route>

          <Route exact path="/post">
            <NewPost></NewPost>
          </Route>
          <Route exact path="/post/:id">
            <Postpage posts={allposts} deletepost={deletepost}></Postpage>
          </Route>
          <Route path="/about" component={About}/>
          <Route path="*" component={Missing}/>

        </Switch>

      </Router>

      <Footer/>
    </div>
  );
}

export default App;
