import React, { useEffect } from 'react'
import Header from './Header'
import Nav from './Nav'
import Footer from './Footer'
import Home from './Home'
import NewPost from './NewPost'
import Postpage from './Postpage'
import Missing from './Missing'
import About from './About'
import {useState} from 'react'
import {  Route, Switch} from 'react-router-dom';
import { useHistory } from 'react-router-dom';
import api from './Api/posts'

function App() {


  const [allposts,setAllposts]=useState([])
  const history=useHistory();
  const [searchvalue,setSearchvalue]=useState("")
  const deletepost=async (e,id)=>{
    e.preventDefault()
    try{
        const response=await api.delete(`/posts/${id}`)
        console.log(response)
        const newposts=allposts.filter((ele)=>ele.id!==id)
        setAllposts(newposts)
        setSearch([])
        setSearchvalue("")
        history.push('/')
    }
    catch(err){
      console.log(err)
    }
  }
  const editpost=async (e,post)=>{
    e.preventDefault()
    try{
        const updated={id:post.id,timeAndDate:new Date().toLocaleDateString,title:post.title,body:post.body}
        const response=await api.put(`/posts/${post.id}`,updated)
        console.log(response)
        setAllposts(allposts.map((ele)=>ele.id==post.id?updated:ele))
        setSearchvalue("")
        history.push('/')
    }
    catch(err){
      console.log(err)
    }
  }
  const [search,setSearch]=useState([])
  useEffect(()=>{
    const getdata=(async ()=>{
      try{
        const response=await api.get("/posts")
        setAllposts(response.data)
      }
      catch(err){
        setAllposts([{id:1,title:"Error ocuured while Fetching data",body:`${err}`}])
      }
    })
    getdata()

  },[])

return (
    <div className="App mt-3 col-lg-5 col-md-10 col-sm-10 shadow-lg border  mx-auto ">
      <Header/>
      <Nav setSearch={setSearch} allposts={allposts} searchvalue={searchvalue} setSearchvalue={setSearchvalue}/>

        <Switch >
          <Route  exact path="/" >
            {search.length>0&&<Home posts={search}></Home>}
            {search.length===0 && allposts.length>0&&<Home posts={allposts}></Home>}
            {allposts.length===0&&<p className='d-flex align-items-center justify-content-center spacing text-center  fs-3 text-primary'>We dont have any posts ...Feel free to create yours</p>}
          </Route>
          <Route exact path="/post">
            <NewPost allposts={allposts} setAllposts={setAllposts} ></NewPost>
          </Route>
          <Route exact path="/post/:id">
            <Postpage posts={allposts} deletepost={deletepost} editpost={edit}></Postpage>
          </Route>
          <Route path="/about" component={About}/>
          <Route path="*" component={Missing}/>

        </Switch>



      <Footer/>
    </div>
  );
}

export default App;
