import React from 'react'
function Postcreation({post}) {
  const display=(e)=>{
    e.preventDefault()
    window.location.href=`post/${post.id}`
    
  }
  return (
    <div className='' >
        <div className='border-bottom border-bottom-2 ps-4 d-flex flex-column mt-1 row-gap-0'>
            <h2 className='mb-1 cur' onClick={(e)=>display(e)} ><Link></Link>{post.title}</h2>
            <p className='mb-1'>{post.timeAndDate}</p>
            <p className='mb-1'>
                {post.body.length >=25?
                    post.body.slice(0,25)+" . . ."
                    :post.body}
            </p>
        </div>
    </div>
  )
}

export default Postcreation
