import React, { useEffect } from 'react'
import Header from './Header'
import Nav from './Nav'
import Footer from './Footer'
import Home from './Home'
import NewPost from './NewPost'
import Postpage from './Postpage'
import Missing from './Missing'
import About from './About'
import {useState} from 'react'
import {  Route, Switch} from 'react-router-dom';
import { useHistory } from 'react-router-dom';
import api from './Api/posts'
import { DataProvider } from './context/DataContext'

function App() {
  const[loading,setloading]=useState(true)
  const [allposts,setAllposts]=useState([])
  const history=useHistory();
  const [searchvalue,setSearchvalue]=useState("")
  const[err,setErr]=useState(false)
  const deletepost=async (e,id)=>{
    e.preventDefault()
    try{
        const response=await api.delete(`/posts/${id}`)
        console.log(response)
        const newposts=allposts.filter((ele)=>ele.id!==id)
        setAllposts(newposts)
        setSearch([])
        setSearchvalue("")
        history.push('/')
    }
    catch(err){
      console.log(err)
    }
  }

  const [search,setSearch]=useState([])
  useEffect(()=>{
    setTimeout(() => {
      setloading(false)
    }, 2000);
    const getdata=(async ()=>{
      try{
        const response=await api.get("/posts")
        setAllposts(response.data)
      }
      catch(err){
        setErr(err.message)
      }
    })
    getdata()

  },[])

return (
    <div className="App mt-3 col-lg-5 col-md-10 col-sm-10 shadow-lg border  mx-auto ">
      <DataProvider>
          <Header width/>
          <Nav setSearch={setSearch} allposts={allposts} searchvalue={searchvalue} setSearchvalue={setSearchvalue}/>

            <Switch >
              <Route  exact path="/" >
                {err&&<p className='d-flex align-items-center  spacing fs-3 text-danger justify-content-center'>{err}</p>}
                {loading && <p className='d-flex align-items-center  spacing fs-3 text-warning justify-content-center'> Data is Loading....</p>}
                {!err && !loading && search.length>0&&<Home posts={search}></Home>}
                {!err && !loading && search.length===0 && allposts.length>0&&<Home posts={allposts}></Home>}
                {!err && !loading && allposts.length===0&&<p className='d-flex align-items-center justify-content-center spacing text-center  fs-3 text-primary'>We dont have any posts ...Feel free to create yours</p>}
              </Route>
              <Route exact path="/post">
                <NewPost allposts={allposts} setAllposts={setAllposts} ></NewPost>
              </Route>
              <Route exact path="/post/:id">
                <Postpage posts={allposts} deletepost={deletepost} ></Postpage>
              </Route>
              <Route path="/about" component={About}/>
              <Route path="*" component={Missing}/>

            </Switch>
            <Footer/>
      </DataProvider>
    </div>
  );
}

export default App;
