import React from 'react'
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';

function NewPost({allposts,setAllposts}) {
  const history=useHistory()
  const Submitdata=(e)=>{
    e.preventDefault();
    const id=allposts.length+1
    const title=document.getElementById('title').value
    const body=document.getElementById('post').value
    const current=new Date()
    const obj={id:id,timeAndDate:current.toLocaleString(),title:title,body:body}
    const newdata=[...allposts,obj]
    setAllposts(newdata)
    history.push('/')
  }


  return (
    <div className='spacing mt-3 ms-3 me-3'>
        <h2 className=''>New Post</h2>
        <div className='mb-2'>
          <p className='mb-1'> Title :</p>
          <input className='col-12' id='title'></input>
        </div>
        <div className=' h-50  mb-5'>
          <p className='mb-1'> Post :</p>
          <textarea className='col-12 d-flex   h-100 ' id='post' placeholder='dd' ></textarea>
        </div>
        <button className='btn col-12 border bg-success  text-white outkine' onClick={(e)=>Submitdata(e)}>Submit</button>
    </div>
  )
}

export default NewPost
