import React from 'react'
import { useParams} from 'react-router-dom/cjs/react-router-dom.min';

function Postpage({posts,deletepost,editpost}) {
  console.log(posts)
  const {id}= useParams();
  const post=posts.filter((ele)=>ele.id==id)
  console.log(post)
  return (
    <div className='spacing overflow-auto ms-3 '>
      <h2>{post[0].title}</h2>
      <p>{post[0].timeAndDate}</p>
      <p>
        {post[0].body}
      </p>
      <button className='col-3 btn btn-danger' onClick={(e)=>{deletepost(e,post[0].id) }}>Delete Post</button>
      <button className='col-3 btn btn-success' onClick={(e)=>{editpost(e,post[0])}} >Edit Post</button>
    </div>
  )
}

export default Postpage
