    const {allposts,deletepost}=useContext(DataContext)
    const edithandler=(e)=>{
        e.preventDefault();
        history.push(`/edit`)


    }
  return (
    <div className='spacing mt-3 ms-3 me-3'>
        <h2 className=''>Edit Post</h2>
        <div className='mb-2'>
          <p className='mb-1'> Title :</p>
          <input className='col-12' id='title'></input>
        </div>
        <div className=' h-50  mb-5'>
          <p className='mb-1'> Post :</p>
          <textarea className='col-12 d-flex h-100 ' id='post' ></textarea>
        </div>
        <button className='btn col-12 border bg-success  text-white' onClick={(e)=>Edit(e)}>Submit</button>
    </div>
  )
}

export default Edit
